                        const name1 = getParticipantName(entry.fields.teilnehmer_1, userMap);
                        const name2 = getParticipantName(entry.fields.teilnehmer_2, userMap);

                        let dateLabel = '—';
                        let timeLabel = '';
                        if (entry.fields.datum_von) {
